padding: 2rem 0px;
$spacer: 1rem;

.location-name {
  font-weight: bold;
}

//displays phonenumber as button on smaller devices
.phone-link {
  @media (min-width: 768px) {
    display: none;
  }
}

.phone-text {
  @media (max-width: 768px) {
    display: none;
  }
}

.arrow-img {
    transform: rotate(-180deg);
    margin-bottom: 10px;
}

.collapsed .arrow-img {
    transform: rotate(0deg);
    margin-bottom: 0px;
}

.info-header {

}

.hours-header {

}

.primary-open-today {

}

.secondary-hours-header {
    margin-top: $spacer;
}

.secondary-open-today {
    font-size: 16px;
    text-decoration: none;
    color: inherit;
}

.arrow-img {
    margin-left: 0.5rem;
    padding-bottom: 5px;
}

.ctas {
  margin-top: 1rem;

  .btn {
    @media (min-width: 768px) {
      margin-bottom: 0.25rem;

      &:not(:last-child) {
        margin-right: 0.25rem;
      }
    }
    @media (max-width: 768px) {
      display: block;
      margin-bottom: 0.25rem;
    }
  }

  .directions-cta {}

  .secondary-cta {}
}

//display the current day as bold
.hours {
  .hours-row-today {
    font-weight: bold;
  }

  .hours-label {
    vertical-align: top;
    padding-right: 10px;
  }

}

.map {
  height: 300px;
}

